i18n.use(reactI18next.initReactI18next);
function getValidLanguage(lang) {
  if (lang === "fa" || lang === "en") {
    return lang;
  }
  return "fa";
}
const initializeI18n = (lang = "fa", resources) => {
  i18n.init({
    resources: {
      fa: {
        translation: { ...fa, ...resources?.fa }
      },
      en: {
        translation: { ...en, ...resources?.en }
      }
    },
    lng: getValidLanguage(lang),
    fallbackLng: "fa",
    debug: false
  });
};
